import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import studentList from './components/Students';
import Button from './components/ButtonNoRep';

class App extends Component {

  //functions
  pickRandomStudent = () => {
    alert ('a random student was chosen');
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={'https://i.pinimg.com/originals/c5/67/60/c56760404cac5ca001c2ba11e330d606.jpg'} className="App-logo" alt="logo" />
          <p>
            Click <code>the icon</code> to pick a Pleb.
          </p>
          <div>
          <Button handleClicked ={this.pickRandomStudent}/>
        </div>
          <a
            className="App-link"
            href="https://medium.com/applab"
            target="_blank"
            rel="noopener noreferrer"
          >
            Plato's Plebians
          </a>
        </header>
      </div>
    );
  }
}

export default App;
